import logging
from fastapi import APIRouter, HTTPException
from celery.result import AsyncResult
from src.workers.celery_app import celery_app

logger = logging.getLogger(__name__)

router = APIRouter(prefix="/tasks", tags=["tasks"])


@router.get("/{task_id}")
async def get_task_status(task_id: str):
    """
    Get the status of a background task.

    Args:
        task_id: The ID of the task to check

    Returns:
        dict: Task status and metadata
    """
    try:
        task_result = AsyncResult(task_id, app=celery_app)

        if task_result.state == 'PENDING':
            # Task is waiting to be processed
            response = {
                "task_id": task_id,
                "status": "pending",
                "message": "Task is waiting to be processed"
            }
        elif task_result.state == 'PROCESSING':
            # Task is currently being processed
            response = {
                "task_id": task_id,
                "status": "processing",
                "message": task_result.info.get('status', 'Processing...'),
                "meta": task_result.info
            }
        elif task_result.state == 'SUCCESS':
            # Task completed successfully
            response = {
                "task_id": task_id,
                "status": "completed",
                "message": "Task completed successfully",
                "result": task_result.result
            }
        elif task_result.state == 'FAILURE':
            # Task failed
            response = {
                "task_id": task_id,
                "status": "failed",
                "message": "Task failed",
                "error": str(task_result.info),
                "meta": task_result.info if hasattr(task_result.info, 'get') else {}
            }
        else:
            # Unknown state
            response = {
                "task_id": task_id,
                "status": task_result.state.lower(),
                "message": f"Task state: {task_result.state}"
            }

        return response

    except Exception as e:
        logger.error(f"Error checking task status for {task_id}: {e}")
        # Special handling for Celery backend serialization errors
        if "Exception information must include" in str(e):
            # Try to get basic task info without full deserialization
            try:
                task_result = AsyncResult(task_id, app=celery_app)
                # Return a degraded response with available info
                return {
                    "task_id": task_id,
                    "status": "error",
                    "message": "Task status unavailable due to backend error",
                    "state": task_result.state if hasattr(task_result, 'state') else 'UNKNOWN',
                    "backend_error": str(e)
                }
            except Exception:
                pass
        raise HTTPException(status_code=500, detail="Error checking task status")


@router.get("/{task_id}/result")
async def get_task_result(task_id: str):
    """
    Get the result of a completed task.

    Args:
        task_id: The ID of the task to get results for

    Returns:
        dict: Task result in the same format as the original synchronous endpoint
    """
    try:
        task_result = AsyncResult(task_id, app=celery_app)

        if task_result.state == 'PENDING':
            raise HTTPException(status_code=202, detail="Task is still pending")
        elif task_result.state == 'PROCESSING':
            raise HTTPException(status_code=202, detail="Task is still processing")
        elif task_result.state == 'SUCCESS':
            # Return the result in the original endpoint format
            return task_result.result
        elif task_result.state == 'FAILURE':
            # Return error in similar format to original endpoint
            error_info = task_result.info if hasattr(task_result.info, 'get') else {}

            if isinstance(error_info, dict):
                error_response = {
                    "error": error_info.get('error', str(task_result.info)),
                    "input_type": error_info.get('input_type', 'unknown'),
                    "original_input": error_info.get('original_input', ''),
                    "conversion_successful": False
                }

                # Return appropriate HTTP status based on error type
                if "not found" in error_response["error"].lower():
                    raise HTTPException(status_code=404, detail=error_response)
                elif "api" in error_response["error"].lower():
                    raise HTTPException(status_code=502, detail=error_response)
                else:
                    raise HTTPException(status_code=400, detail=error_response)
            else:
                raise HTTPException(status_code=500, detail={"error": str(task_result.info)})
        else:
            raise HTTPException(status_code=500, detail=f"Unknown task state: {task_result.state}")

    except HTTPException:
        raise
    except Exception as e:
        logger.error(f"Error getting task result for {task_id}: {e}")
        # Special handling for Celery backend serialization errors
        if "Exception information must include" in str(e):
            raise HTTPException(
                status_code=503, 
                detail={
                    "error": "Task result unavailable due to backend error",
                    "backend_error": str(e),
                    "task_id": task_id
                }
            )
        raise HTTPException(status_code=500, detail="Error retrieving task result")


@router.delete("/{task_id}")
async def cancel_task(task_id: str):
    """
    Cancel a running or pending task.

    Args:
        task_id: The ID of the task to cancel

    Returns:
        dict: Cancellation status
    """
    try:
        task_result = AsyncResult(task_id, app=celery_app)

        if task_result.state in ['PENDING', 'PROCESSING']:
            task_result.revoke(terminate=True)
            return {
                "task_id": task_id,
                "status": "cancelled",
                "message": "Task has been cancelled"
            }
        elif task_result.state == 'SUCCESS':
            return {
                "task_id": task_id,
                "status": "completed",
                "message": "Task already completed, cannot cancel"
            }
        elif task_result.state == 'FAILURE':
            return {
                "task_id": task_id,
                "status": "failed",
                "message": "Task already failed, cannot cancel"
            }
        else:
            return {
                "task_id": task_id,
                "status": task_result.state.lower(),
                "message": f"Task state: {task_result.state}"
            }

    except Exception as e:
        logger.error(f"Error cancelling task {task_id}: {e}")
        raise HTTPException(status_code=500, detail="Error cancelling task")
